INSERT INTO checklist_templates (id, type, title, version) VALUES
    ('11111111-1111-1111-1111-111111111111', 'OPENING', 'Организация строительства', 0);

INSERT INTO template_sections (id, template_id, title, order_index, version) VALUES
    ('22222222-2222-2222-2222-222222222222', '11111111-1111-1111-1111-111111111111',
     'Наличие разрешительной, организационно-технологической, рабочей документации.', 1, 0),
    ('33333333-3333-3333-3333-333333333333', '11111111-1111-1111-1111-111111111111',
     'Инженерная подготовка строительной площадки.', 2, 0);

-- Секция 1
INSERT INTO template_items (id, section_id, item_number, text, order_index, required, version) VALUES
    ('aaaaaaa1-aaaa-aaaa-aaaa-aaaaaaaaaaa1', '22222222-2222-2222-2222-222222222222', '1.1',
     E'Наличие приказа на ответственное лицо, осуществляющего строительство (производство работ).\n(п. 5.3. СП 48.13330.2019. Изм. №1. Организация строительства)', 1, false, 0),
    ('aaaaaaa2-aaaa-aaaa-aaaa-aaaaaaaaaaa2', '22222222-2222-2222-2222-222222222222', '1.2',
     E'Наличие приказа на ответственное лицо, осуществляющее строительный контроль (с указанием идентификационного номера в НРС в области строительства).\n(п. 5.3. СП 48.13330.2019. Изм. №1. Организация строительства)', 2, false, 0),
    ('aaaaaaa3-aaaa-aaaa-aaaa-aaaaaaaaaaa3', '22222222-2222-2222-2222-222222222222', '1.3',
     E'Наличие приказа на ответственное лицо, осуществляющее подготовку проектной документации, авторский надзор.\n(п. 5.3. СП 48.13330.2019. Изм. №1. Организация строительства)', 3, false, 0),
    ('aaaaaaa4-aaaa-aaaa-aaaa-aaaaaaaaaaa4', '22222222-2222-2222-2222-222222222222', '1.4',
     E'Наличие проектной документации со штампом «В производство работ».\n(п. 5.5. СП 48.13330.2019. Изм. №1. Организация строительства)', 4, false, 0),
    ('aaaaaaa5-aaaa-aaaa-aaaa-aaaaaaaaaaa5', '22222222-2222-2222-2222-222222222222', '1.5',
     E'Наличие проекта производства работ (утвержденного руководителем подрядной организации, согласованного Заказчиком, проектировщиком, эксплуатирующей организацией).\n(п. 6.4., п. 6.7., п. 6.9. СП 48.13330.2019. Изм. №1. Организация строительства).', 5, false, 0);

-- Секция 2
INSERT INTO template_items (id, section_id, item_number, text, order_index, required, version) VALUES
    ('bbbbbbb1-bbbb-bbbb-bbbb-bbbbbbbbbbb1', '33333333-3333-3333-3333-333333333333', '2.1',
     E'Наличие акта геодезической разбивочной основы, принятых знаков (реперов).\n(п. 7.2. СП 48.13330.2019. Изм. №1. Организация строительства).', 1, false, 0),
    ('bbbbbbb2-bbbb-bbbb-bbbb-bbbbbbbbbbb2', '33333333-3333-3333-3333-333333333333', '2.2',
     E'Наличие генерального плана (ситуационного плана).\n(п. 7.6. СП 48.13330.2019. Изм. №1. Организация строительства).', 2, false, 0),
    ('bbbbbbb3-bbbb-bbbb-bbbb-bbbbbbbbbbb3', '33333333-3333-3333-3333-333333333333', '2.3',
     E'Фактическое размещение временной инженерной и бытовой инфраструктуры площадки (включая стоянку автотранспорта) согласно проекту организации. Соответствие размещённых временных инфраструктуры требованиям электробезопасности, пожарных, санитарно-эпидемиологических норм и правил.\n(п. 7.10., п. 7.34. СП 48.13330.2019. Изм. №1. Организация строительства).', 3, false, 0),
    ('bbbbbbb4-bbbb-bbbb-bbbb-bbbbbbbbbbb4', '33333333-3333-3333-3333-333333333333', '2.4',
     E'Наличие пунктов очистки или мойки колес транспортных средств на выездах со строительной площадки.\n(п. 7.13. СП 48.13330.2019. Изм. №1. Организация строительства).', 4, false, 0),
    ('bbbbbbb5-bbbb-bbbb-bbbb-bbbbbbbbbbb5', '33333333-3333-3333-3333-333333333333', '2.5',
     E'Наличие бункеров или контейнеров для сбора отдельно бытового и отдельно строительного мусора.\n(п. 7.13. СП 48.13330.2019. Изм. №1. Организация строительства).', 5, false, 0),
    ('bbbbbbb6-bbbb-bbbb-bbbb-bbbbbbbbbbb6', '33333333-3333-3333-3333-333333333333', '2.6',
     E'Наличие информационных щитов (знаков) с указанием:\n- наименование объекта;\n- наименование Застройщика (технического Заказчика);\n- наименование подрядной организации;\n- наименование проектной организации;\n- сроки строительства;\n- контактные телефоны ответственных по приказу лиц по организации.\n(п. 7.13. СП 48.13330.2019. Изм. №1. Организация строительства).', 6, false, 0),
    ('bbbbbbb7-bbbb-bbbb-bbbb-bbbbbbbbbbb7', '33333333-3333-3333-3333-333333333333', '2.7',
     E'Наличие стендов пожарной безопасности с указанием на схеме мест источников воды, средств пожаротушения.\n(п. 7.13. СП 48.13330.2019. Изм. №1. Организация строительства).', 7, false, 0);

INSERT INTO checklist_templates (id, type, title, version) VALUES
