INSERT INTO template_sections (id, template_id, title, order_index, version) VALUES
    ('88888888-8888-8888-8888-888888888888', '99999999-9999-9999-9999-999999999999',
     '1. Соблюдение требований безлпастности труда. Культура производства', 1, 0),
    ('77777777-7777-7777-7777-777777777777', '99999999-9999-9999-9999-999999999999',
     '2. Производство работ по устройству бордюрных камней', 2, 0),
    ('66666666-6666-6666-6666-666666666666', '99999999-9999-9999-9999-999999999999',
     '3. Бетонные работы. Устройство бетонных дорог', 3, 0),
    ('55555555-5555-5555-5555-555555555555', '99999999-9999-9999-9999-999999999999',
     '4. Асфальтобетонные дороги, тротуары из асфальтобетонного покрытия', 4, 0),
    ('44444444-4444-4444-4444-444444444444', '99999999-9999-9999-9999-999999999999',
