-- Секция 1
INSERT INTO template_items (id, section_id, item_number, text, order_index, required, version) VALUES
    ('ccccccc0-cccc-cccc-cccc-cccccccc0001','88888888-8888-8888-8888-888888888888','1.1',
     E'Оформление наряд-допуска на безопасное проведение работ в местах охранной зоны:\n- линии электропередач и прилегающих объектов электроэнергетики;\n- линии связи и прилегающих сооружений связи;\n- прилегающей зоны железной дороги;\n- трубопроводов (газопроводы, нефтепроводы и т.п.);\n- объекта культурного наследия;\n- военного объекта;\n- государственного природного заповедника (включая национальные парки и памятники природы).\n(п. 4.6., 4.11., 4.11.2., 4.11.3., 4.11.4. СНиП 12-03-2001 «Безопасность труда в строительстве»; ст. 105 Земельного кодекса РФ №136-ФЗ).',
     1,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0002','88888888-8888-8888-8888-888888888888','1.2',
     E'Наличие устойчивого ограждения из секций КГХ с гирляндой фонарей аварийного освещения по периметру зоны работ (со сроком более 2-х дней в пешеходной зоне, более 7 дней в зоне проезжей части). Ширина прохода пешеходов между выгородками КГХ не менее 1,5 м.\n(п. 6.2.2. СНиП 12-03-2001; ГОСТ Р 58967-2020 «Ограждения инвентарные...»; п. 4.9 СП 48.13330.2019, изм. №2).',
     2,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0003','88888888-8888-8888-8888-888888888888','1.3',
     E'Наличие крепких пешеходных деревянных настилов (шириной не менее 1,5 м) в местах прохода по нетвёрдым покрытиям и газонам.\nНаличие пандусов при перепаде высот свыше 10 см на пути следования пешеходов.\n(НТД).',
     3,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0004','88888888-8888-8888-8888-888888888888','1.4',
     E'Ограждения КГХ и водоналивные блоки не имеют загрязнений, повреждений, деформаций, вандальных надписей. (НТД).',
     4,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0005','88888888-8888-8888-8888-888888888888','1.5',
     E'Наличие читаемых навигационных указателей для движения пешеходов при выходе из подземных переходов, метро, разветвлениях и пр. (НТД).',
     5,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0006','88888888-8888-8888-8888-888888888888','1.6',
     E'Полиэтиленовые трубы.\n- Полиэтиленовые трубы уложены в штабель, высотой не более 3-х метров на инвентарные подкладки.\n- Соприкосновение с грунтом не допускается.\n- Нижний ряд труб укреплён инвентарными башмаками или концевыми упорами.\n- Штабель с полиэтиленовыми трубами установлен устойчиво, без перекосов.\nИнертные, строительные материалы (песок, щебень). \n- Материал в удалённой зоне от границ тротуаров, проезжей части.\n- Материал не смешан с другими материалами по неоднородности.\n- Организованы временные ограждения материалов.',
     6,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0007','88888888-8888-8888-8888-888888888888','1.7',
     E'В местах заезда техники к участкам производства работ организованы «карманы»/«воротнички» для безопасного производства работ. (НТД).',
     7,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0008','88888888-8888-8888-8888-888888888888','1.8',
     E'Закрытие штроб, траншей крепкими пешеходными деревянными настилами в зоне остановок общественного транспорта (допускается временная засыпка асфальтобетонной крошкой, щебнем мелкой фракции, заделка бетоном в отметку верха бортового камня проезжей части). (НТД).',
     8,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0009','88888888-8888-8888-8888-888888888888','1.9',
     E'Проход пешеходов к остановкам общественного транспорта без выхода на проезжую часть. (НТД).',
     9,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0010','88888888-8888-8888-8888-888888888888','1.10',
     E'Наличие защитного ограждения зелёных насаждений:\n- деревья защищены сплошными деревянными щитами высотой 2 м на расстоянии не более 0,5 м от ствола;\n- кустарники защищены деревянными щитами высотой не менее 1 м.\n(НТД).',
     10,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0011','88888888-8888-8888-8888-888888888888','1.11',
     E'Оголённая корневая система зелёных насаждений укрыта геотекстилем (дорнитом) либо засыпана плодородным грунтом. (НТД).',
     11,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0012','88888888-8888-8888-8888-888888888888','1.12',
     E'Ограждение складируемого материала либо строительного мусора (объёмом не более 1 кузова самосвала) согласно п. 1.2. (п. 6.2.9 СНиП 12-03-2001).',
     12,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0013','88888888-8888-8888-8888-888888888888','1.13',
     E'Наличие защитного ограждения сохраняемой облицовки зданий и сооружений (парапеты, стены, цоколи входа/выхода метро, подземных пешеходных переходов, остановок и т.д.).\nОграждение выполняется из фанеры либо сплошных деревянных щитов.\n(п. 6.2.2 СНиП 12-03-2001; ГОСТ Р 58967-2020; п. 4.9 СП 48.13330.2019, изм. №1).',
     13,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0014','88888888-8888-8888-8888-888888888888','1.14',
     E'Своевременный вывоз строительного мусора (объёмом более 1 кузова самосвала), а также демонтируемого оборудования и МАФ. (НТД).',
     14,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0015','88888888-8888-8888-8888-888888888888','1.15',
     E'Наличие оперативной бригады для содержания ограждения зон производства работ согласно п. 1.2.\nОтсутствие бытового мусора в выгородках. (НТД).',
     15,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0016','88888888-8888-8888-8888-888888888888','1.16',
     E'Складирование материалов в соответствии с НТД.\nБордюрный камень:\n- на поддонах, штабеля высотой ≤ 2 м;\n- расстояние между штабелями ≥ 50 см;\n- устойчивое основание, без перекосов;\n- исключено соприкосновение с грунтом.\nПолиэтиленовые трубы:\n- в штабелях ≤ 3 м на инвентарных подкладках;\n- без соприкосновения с грунтом;\n- нижний ряд — на инвентарных башмаках/упорах;\n- штабель установлен устойчиво, без перекосов.\nИнертные и строительные материалы (песок, щебень):\n- размещены вдали от границ тротуаров/проезжей части;\n- не смешаны с другими материалами;\n- организованы временные ограждения.\n(п. 6.3 «Безопасность труда в строительстве» и др.).',
     16,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0017','88888888-8888-8888-8888-888888888888','1.17',
     E'Обеспечение электробезопасности (нет оголённых проводов, треснутых розеток и т.п.) и пожаробезопасности (наличие огнетушителя при сварочных работах) на местах производства работ.\n(пп. 6.4, 6.5 «Безопасность труда в строительстве»).',
     17,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0018','88888888-8888-8888-8888-888888888888','1.18',
     E'Строительная техника исправна, имеет звуковой сигнал при движении задним ходом.\n(п. 7.2 «Безопасность труда в строительстве»).',
     18,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0019','88888888-8888-8888-8888-888888888888','1.19',
     E'Инструмент, оснастка, приспособления, тросы, леса и прочее для выполнения работ — в исправном состоянии.\n(п. 7.4 «Безопасность труда в строительстве»).',
     19,false,0),
    ('ccccccc0-cccc-cccc-cccc-cccccccc0020','88888888-8888-8888-8888-888888888888','1.20',
     E'Обеспечение работников СИЗ (минимальный комплект).\nДля водителя-механизатора:\n- сигнальный жилет.\nДля монтажника/разнорабочего/стропальщика:\n- спецодежда;\n- спецобувь;\n- сигнальный жилет;\n- рукавицы (перчатки с обливом);\n- каска (при погрузочно-разгрузочных работах);\n- защитные очки (при работе с УШМ).\nДля сварщика:\n- сварочная роба;\n- спецобувь;\n- каска-маска;\n- шлем-косынка;\n- рукавицы-краги/защитные перчатки;\n- наколенники.\n(Приказ Минтруда РФ от 29.10.2021 №766н).',
     20,false,0);

-- Секция 2
INSERT INTO template_items (id, section_id, item_number, text, order_index, required, version) VALUES
    ('ddddddd0-dddd-dddd-dddd-dddddddd0001','77777777-7777-7777-7777-777777777777','2.1',
     E'Сколы на бордюрных камнях свыше допустимых значений (свыше 10 мм сколы не допускаются).\n(п. 6.5.2, табл. Б4, прил. Б ГОСТ 50597-2017).',
     1,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0002','77777777-7777-7777-7777-777777777777','2.2',
     E'Планово-высотное смещение бордюрных камней на стыках не более 5 мм.\n(п. 6.25 СП 82.13330.2016 «Благоустройство территорий»).',
     2,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0003','77777777-7777-7777-7777-777777777777','2.3',
     E'Зазоры на стыках бордюрных камней (включая толщину компенсатора) не более 5 мм.\n(п. 6.5.2, табл. Б4, прил. Б ГОСТ 50597-2017).',
     3,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0004','77777777-7777-7777-7777-777777777777','2.4',
     E'При наличии зазоров (до 10 мм) между бордюрными камнями шов заполнен бетонным раствором класса не ниже В30.\n(п. 6.25 СП 82.13330.2016).',
     4,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0005','77777777-7777-7777-7777-777777777777','2.5',
     E'Прямолинейная установка бортовых камней.\n(п. 6.25 СП 82.13330.2016).',
     5,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0006','77777777-7777-7777-7777-777777777777','2.6',
     E'Установка бордюрных камней выполнена выше/ниже уровня профиля газона, асфальтобетонного покрытия, тротуарной плитки (допускается до 5 см).\n(п. 6.25 СП 82.13330.2016).',
     6,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0007','77777777-7777-7777-7777-777777777777','2.7',
     E'Выступ бортового камня за пределы КДО.\n(п. 6.25 СП 82.13330.2016).',
     7,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0008','77777777-7777-7777-7777-777777777777','2.8',
     E'Применены непроектные элементы (углы, радиусы) бордюрного камня.\n(Проектное решение).',
     8,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0009','77777777-7777-7777-7777-777777777777','2.9',
     E'Уплотнение песчаного основания виброплитой за 3–4 прохода.\n(п. 7.13 СП 48.13330.2019, изм. №1).',
     9,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0010','77777777-7777-7777-7777-777777777777','2.10',
     E'Размеры бетонной обоймы бортового камня соответствуют проекту; бетон имеет единое тело на всём сечении. Применяется бетон класса не ниже В15; подвижность смеси П-3…П-4 (по погодным условиям).',
     10,false,0),
    ('ddddddd0-dddd-dddd-dddd-dddddddd0011','77777777-7777-7777-7777-777777777777','2.11',
     E'Бетонная обойма бордюрного камня без повреждений; бетон не крошится после набора прочности.\n(п. 6.25 СП 82.13330.2016).',
     11,false,0);

-- Секция 3
INSERT INTO template_items (id, section_id, item_number, text, order_index, required, version) VALUES
    ('eeeeeee0-eeee-eeee-eeee-eeeeeeee0001','66666666-6666-6666-6666-666666666666','3.1',
     E'Подготовка основания:\n- плодородный слой удалён полностью (≈ 10–30 см);\n- укладка геотекстиля (при проектном решении);\n- песчаное основание 15–30 см, уплотнено;\n- щебёночно-гравийная смесь (крупная) 20–40 см, утрамбована;\n- щебёночно-гравийная смесь (мелкая) расклинена, утрамбована;\n- щебёночное основание выравнено мелкой фракцией или бетоном низкого класса;\n- при наличии люков — их уровень соответствует финишному слою, предусмотрены доборные элементы.\n(Проектное решение).',
     1,false,0),
    ('eeeeeee0-eeee-eeee-eeee-eeeeeeee0002','66666666-6666-6666-6666-666666666666','3.2',
     E'Монтаж опалубки:\n- опалубка установлена надёжно, без щелей;\n- нанесена разделительная эмульсия/суспензия;\n- геометрия соответствует проекту; маяки отмечены;\n- при проектном решении уложен армокаркас 12–16 мм.\n(Проектное решение).',
     2,false,0),
    ('eeeeeee0-eeee-eeee-eeee-eeeeeeee0003','66666666-6666-6666-6666-666666666666','3.3',
     E'Бетонирование:\n- толщина покрытия 18–24 см (или по проекту);\n- после заливки смесь уплотнена и выровнена по маякам;\n- во время набора прочности (до 50–60%) поверхность увлажняется.\n(Проектное решение).',
     3,false,0);

-- Секция 4
INSERT INTO template_items (id, section_id, item_number, text, order_index, required, version) VALUES
    ('fffffff0-ffff-ffff-ffff-ffffffffff01','55555555-5555-5555-5555-555555555555','4.1',
     E'Подготовка основания:\n- плодородный слой удалён (≈ 10–100 см);\n- укладка геотекстиля (при проектном решении);\n- песчаное основание 15–40 см, уплотнено;\n- ЩГС (крупная) 20–40 см, утрамбована;\n- ЩГС (мелкая) расклинена, утрамбована;\n- укладка геосетки/георешётки (при проектном решении);\n- при наличии люков — уровень соответствует финишному слою, предусмотрены доборные элементы.\n(Проектное решение).',
     1,false,0),
    ('fffffff0-ffff-ffff-ffff-ffffffffff02','55555555-5555-5555-5555-555555555555','4.2',
     E'Укладка покрытия:\n- при фрезеровании старого покрытия (капремонт) пункт 4.1 не контролируется;\n- проливка основания битумной эмульсией (≤ 3 часов до укладки);\n- укладка первого слоя: t смеси из кузова 120–160°С; проход лёгкого катка ≥ 5 раз по месту; асфальт уплотнён при отсутствии «волны»; t уплотнения 80–120°С;\n- укладка второго слоя: те же требования;\n- бесшовность/однородность обеспечены; деформационные швы отсутствуют;\n- коэффициент уплотнения ≥ 0,93; отбор кернов: 1 шт. на ≤ 2000 м² (по данным лаборатории подрядчика).\n(Проектное решение; п. 6.12 СП 82.13330.2016).',
     2,false,0);

-- Секция 5
INSERT INTO template_items (id, section_id, item_number, text, order_index, required, version) VALUES
    ('99999990-9999-9999-9999-999999990001','44444444-4444-4444-4444-444444444444','5.1',
     E'Подготовка основания:\n- плодородный слой удалён (≈ 0–30 см);\n- укладка геотекстиля (при проектном решении);\n- песчаное основание имеет проектную толщину и уплотнение;\n- при наличии люков — их уровень соответствует уровню плитки, предусмотрены доборные элементы.\n(Проектное решение).',
     1,false,0),
    ('99999990-9999-9999-9999-999999990002','44444444-4444-4444-4444-444444444444','5.2',
     E'Укладка плитки:\n- высотно-плановые перепады на стыках ≤ 5 мм;\n- швы ≤ 5 мм, шов заполнен герметизирующим материалом.\n(п. 6.22 СП 82.13330.2016).',
     2,false,0);

